# Lấy lương cơ bản từ hợp đồng - AN TOÀN với NULL
result = 0.0
if payslip and payslip.contract_id:
    base_wage = payslip.contract_id.base_wage
    if base_wage is not None and base_wage > 0:
        result = float(base_wage)

# Lấy tổng các phúc lợi từ hợp đồng - AN TOÀN với NULL
result = 0.0
if payslip and payslip.contract_id:
    benefits = payslip.contract_id.benefit_ids
    if benefits:
        for b in benefits:
            amount = b.amount
            if amount is not None and amount > 0:
                result += float(amount)

# BHXH = 8% lương cơ bản - CHỈ tính nếu có ghi nhận đóng góp BHXH
result = 0.0

if payslip and payslip.contract_id:
    # Kiểm tra xem hợp đồng có ghi nhận đóng góp BHXH không
    contributions = payslip.contract_id.contribution_ids.filtered(
        lambda c: c.contribution_type_id and c.contribution_type_id.code == 'BHXH'
    )

    # QUAN TRỌNG: Kiểm tra độ dài recordset, không chỉ kiểm tra truthy
    if len(contributions) > 0:
        basic = rules.get('BASIC', 0.0)
        if basic is not None and basic > 0:
            result = -float(basic) * 0.08

